import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../MenuDetails.css';

function MenuDetails() {
  const [showDetails, setShowDetails] = useState({ doubts: false, substitutes: false, shoppingList: false });
  const navigate = useNavigate();
  const location = useLocation();  // Para acessar o estado passado na navega√ß√£o

  const toggleDetails = (detail) => {
    setShowDetails(prev => ({ ...prev, [detail]: !prev[detail] }));
  };

  const day = location.state ? location.state.day : "Dia";  // Pega o dia da semana passado ou usa um valor padr√£o

  return (
    <div className="menu-details-container">
      <div className="sidebar">
        <button onClick={() => navigate(-1)} className="back-button">
          <span>üîô</span>
        </button>
        <input type="text" placeholder="Pesquisar..." className="search-input" />
      </div>
      <div className="content">
        <h2 className="menu-title">Card√°pio de {day}</h2>
        <h3>Almo√ßo:</h3>
        <p>Lorem Ipsum...</p>
        <h3>Jantar:</h3>
        <p>Lorem Ipsum...</p>
        <div className="info-section">
          <ul>
            <li onClick={() => toggleDetails('doubts')}>
              <span className="icon">‚ùì</span> D√∫vidas com o preparo?
            </li>
            {showDetails.doubts && <p>Detalhes sobre como preparar...</p>}
            <li onClick={() => toggleDetails('substitutes')}>
              <span className="icon">üîÑ</span> Alimentos substitutivos.
            </li>
            {showDetails.substitutes && <p>Op√ß√µes para substituir...</p>}
            <li onClick={() => toggleDetails('shoppingList')}>
              <span className="icon">üõí</span> Lista de compras.
            </li>
            {showDetails.shoppingList && <p>O que comprar para preparar...</p>}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MenuDetails;
