import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../MenuDetails.css';

function MenuDetails() {
  const [showDetails, setShowDetails] = useState({ doubts: false, substitutes: false, shoppingList: false });
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const handleSearchChange = async (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    if (query.length > 0) {
      try {
        const response = await fetch(`http://localhost:3301/api/food/search?name=${query}`);
        const data = await response.json();
        setSearchResults(data.map(food => food.nome).join(', '));
      } catch (error) {
        console.error('Search failed:', error);
        setSearchResults('Search failed');
      }
    } else {
      setSearchResults('');
    }
  };

  const day = location.state ? location.state.day : "Dia";

  return (
    <div className="menu-details-container">
      <div className="sidebar">
        <img src="/images/curitiba-logo.png" alt="Curitiba Logo" className="logo"/>
        <button onClick={() => navigate(-1)} className="back-button" aria-label="Voltar">
          <span>ðŸ”™</span>
        </button>
        <input type="text" placeholder="Pesquisar..." className="search-input" value={searchQuery} onChange={handleSearchChange} />
        <div className="search-results">{searchResults}</div>
      </div>
      ...
    </div>
  );
}

export default MenuDetails;
