import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../LoginPage.css'; // Certifique-se de que o caminho est치 correto

function LoginPage() {
  const [cpf, setCpf] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [error, setError] = useState('');
  const [errorField, setErrorField] = useState('');
  const [isInvalidCpf, setIsInvalidCpf] = useState(false);
  const [isInvalidBirthdate, setIsInvalidBirthdate] = useState(false);
  const navigate = useNavigate();

  const handleCpfChange = (event) => {
    const formattedValue = event.target.value.replace(/\D/g, '')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d)/, '$1.$2')
      .replace(/(\d{3})(\d{1,2})/, '$1-$2');
    setCpf(formattedValue);
  };

  const handleBirthdateChange = (event) => {
    const formattedValue = event.target.value.replace(/\D/g, '')
      .replace(/(\d{2})(\d)/, '$1/$2')
      .replace(/(\d{2})(\d)/, '$1/$2');
    setBirthdate(formattedValue);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const cleanedCpf = cpf.replace(/\D/g, '');
    const cleanedBirthdate = birthdate.replace(/\D/g, '');
    if (cleanedCpf.length !== 11 || cleanedBirthdate.length !== 8) {
      setError('Dados incorretos: ' + (cleanedCpf.length !== 11 ? 'CPF' : '') + (cleanedBirthdate.length !== 8 ? ' e Data de Nascimento' : ''));
      setErrorField('Form');
      return;
    }

    try {
      const response = await axios.post('http://localhost:3301/api/login', {
        cpf: cleanedCpf,
        birthdate: cleanedBirthdate
      });
      if (response.data.success) {
        localStorage.setItem('userName', response.data.user.name); // Armazena o nome do usu치rio no localStorage
        navigate('/foodappmain', { state: { userName: response.data.user.name } }); // Passa o nome via state
      } else {
        setError('Dados incorretos: ' + response.data.message);
        setErrorField('Login');
      }
    } catch (error) {
      setError('Erro ao tentar fazer login');
      setErrorField('Servidor');
    }
  };

  return (
    <div className="login-container">
      <img src="/images/curitiba-logo.png" alt="Curitiba Logo" className="logo"/>
      <h1>Bem vindo!</h1>
      <p>Entre com seus dados</p>
      <form onSubmit={handleSubmit}>
        <input 
          type="text"
          id="cpf"
          placeholder="CPF"
          value={cpf}
          onChange={handleCpfChange}
          className={errorField === 'Form' ? 'invalid' : ''}
          maxLength="14"
        />
        <input 
          type="text"
          id="birthdate"
          placeholder="Data de Nascimento"
          value={birthdate}
          onChange={handleBirthdateChange}
          className={errorField === 'Form' ? 'invalid' : ''}
          maxLength="10"
        />
        <button type="submit">ACESSAR</button>
      </form>
      {error && <div className="error-alert">{error}</div>}
      <a href="/reset-password">Esqueceu sua senha?</a>
    </div>
  );
}

export default LoginPage;
