import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Garantir que está importado corretamente
import '../LoginPage.css';

function LoginPage() {
  const [cpf, setCpf] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [error, setError] = useState(''); // Inicializar corretamente o estado de erro
  const [isInvalidCpf, setIsInvalidCpf] = useState(false); // Adicionando estado para validação de CPF
  const [isInvalidBirthdate, setIsInvalidBirthdate] = useState(false); // Adicionando estado para validação de data de nascimento
  const navigate = useNavigate();


  const handleCpfChange = (event) => {
    const originalValue = event.target.value.replace(/\D/g, '');
    let formattedValue = '';
    if (originalValue.length <= 11) {
      formattedValue = originalValue
        .replace(/(\d{3})(\d)/, '$1.$2')
        .replace(/(\d{3})(\d)/, '$1.$2')
        .replace(/(\d{3})(\d{1,2})/, '$1-$2');
    }
    setCpf(formattedValue);
    setIsInvalidCpf(false);
  };

  const handleBirthdateChange = (event) => {
    const originalValue = event.target.value.replace(/\D/g, '');
    let formattedValue = '';
    if (originalValue.length <= 8) {
      formattedValue = originalValue
        .replace(/(\d{2})(\d)/, '$1/$2')
        .replace(/(\d{2})(\d)/, '$1/$2');
    }
    setBirthdate(formattedValue);
    setIsInvalidBirthdate(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (cpf.replace(/\D/g, '').length !== 11 || birthdate.replace(/\D/g, '').length !== 8) {
      setError('Dados incorretos'); // Usar corretamente setError para definir mensagens de erro
      return;
    }

    try {
      const response = await axios.post('http://localhost:3301/api/login', { cpf, birthdate });
      if (response.data.success) {
        navigate('/foodappmain'); // Usar corretamente navigate para redirecionar
      } else {
        setError(response.data.message); // Usar corretamente setError para definir mensagens de erro
      }
    } catch (error) {
      setError('Erro ao tentar fazer login'); // Usar corretamente setError para definir mensagens de erro
    }
  };

  return (
    <div className="login-container">
      <img src="/images/curitiba-logo.png" alt="Curitiba Logo" className="logo"/>
      <h1>Bem vindo!</h1>
      <p>Entre com seus dados</p>
      <form onSubmit={handleSubmit}>
        <input 
          type="text"
          id="cpf"
          placeholder="CPF"
          value={cpf}
          onChange={handleCpfChange}
          className={error ? 'invalid' : ''}
          maxLength="14"
        />
        <input 
          type="text"
          id="birthdate"
          placeholder="Data de Nascimento"
          value={birthdate}
          onChange={handleBirthdateChange}
          className={error ? 'invalid' : ''}
          maxLength="10"
        />
        <button type="submit">ACESSAR</button>
      </form>
      {error && <p className="error">{error}</p>}
      <a href="/reset-password">Esqueceu sua senha?</a>
    </div>
  );
}

export default LoginPage;
