import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../MenuDetails.css';
import { fetchMealDetailsByDay } from '../services/mealService';
import { searchFoodByName } from '../services/foodService';

function MenuDetails() {
  const [activeDetail, setActiveDetail] = useState('');
  const [sidebarVisible, setSidebarVisible] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [alertMessage, setAlertMessage] = useState('');
  const [mealDetails, setMealDetails] = useState({ almo√ßo: '', jantar: '' });
  const navigate = useNavigate();
  const location = useLocation();
  const day = location.state ? location.state.day : "Dia";

  useEffect(() => {
    if (day) {
      fetchMealDetailsByDay(day).then(result => {
        if (result.length > 0) {
          setMealDetails({ almo√ßo: result[0].components, jantar: result[0].components });
        } else {
          setMealDetails({ almo√ßo: 'Informa√ß√£o n√£o dispon√≠vel', jantar: 'Informa√ß√£o n√£o dispon√≠vel' });
        }
      }).catch(error => {
        console.error('Failed to fetch meal details:', error);
        setMealDetails({ almo√ßo: 'Erro ao carregar', jantar: 'Erro ao carregar' });
      });
    }
  }, [day]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setAlertMessage('');
  };

  const handleSearch = async () => {
    if (searchQuery.length > 0) {
      try {
        const data = await searchFoodByName(searchQuery);
        if (data.length === 0) {
          setAlertMessage('Alimento n√£o encontrado');
          setSearchResults([]);
        } else {
          setSearchResults(data.slice(0, 3));
          setSearchQuery('');
        }
      } catch (error) {
        console.error('Search failed:', error);
        setAlertMessage('Search failed');
      }
    }
  };

  const removeItem = id => {
    setSearchResults(prev => prev.filter(item => item.id !== id));
  };

  return (
    <div className="menu-details-container">
      <div className={`sidebar ${sidebarVisible ? 'visible' : ''}`}>
        {sidebarVisible && (
          <>
            <img src="/images/curitiba-logo.png" alt="Curitiba Logo" className="logo"/>
            <button onClick={() => navigate(-1)} className="back-button">üîô</button>
            <input type="text" placeholder="Pesquisar..." className="search-input" value={searchQuery} onChange={handleSearchChange} />
            <button onClick={handleSearch} className="search-button">Adicionar</button>
            {alertMessage && <div className="alert">{alertMessage}</div>}
            <div className="search-results">
              {searchResults.map(item => (
                <div key={item.id}>
                  {item.nome} <button onClick={() => removeItem(item.id)} className="remove-button">X</button>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
      <div className="content">
        <h2 className="menu-title">Card√°pio de {day}</h2>
        <h3>Almo√ßo:</h3>
        <p>{mealDetails.almo√ßo}</p>
        <h3>Jantar:</h3>
        <p>{mealDetails.jantar}</p>
        <div className="info-section">
          <ul>
            <li onClick={() => toggleDetails('doubts')}>
              <span className="icon" aria-hidden="true">‚ùì</span> D√∫vidas com o preparo?
              {activeDetail === 'doubts' && <p>Detalhes sobre como preparar...</p>}
            </li>
            <li onClick={() => toggleDetails('substitutes')}>
              <span className="icon" aria-hidden="true">üîÑ</span> Alimentos substitutivos.
              {activeDetail === 'substitutes' && <p>Op√ß√µes para substituir...</p>}
            </li>
            <li onClick={() => toggleDetails('shoppingList')}>
              <span className="icon" aria-hidden="true">üõí</span> Lista de compras.
              {activeDetail === 'shoppingList' && <p>O que comprar para preparar...</p>}
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MenuDetails;
