import React, { useState } from 'react';
import axios from 'axios'; // Importação do Axios
import { useNavigate } from 'react-router-dom';
import '../LoginPage.css'; // Ajuste o caminho se necessário

function LoginPage() {
  const [cpf, setCpf] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:3301/api/login', { cpf, birthdate });
      if (response.data.success) {
        navigate('/foodappmain'); // Redirecionar para a página principal após o login
      } else {
        setError('Dados incorretos'); // Exibir mensagem de erro
      }
    } catch (error) {
      setError('Erro ao tentar fazer login');
    }
  };

  return (
    <div className="login-container">
      <img src="/images/curitiba-logo.png" alt="Curitiba Logo" className="logo"/>
      <h1>Bem vindo!</h1>
      <p>Entre com seus dados</p>
      <form onSubmit={handleLogin}>
        <input 
          type="text"
          id="cpf"
          placeholder="CPF"
          value={cpf}
          onChange={e => setCpf(e.target.value)}
          className={error ? 'invalid' : ''}
        />
        <input 
          type="text"
          id="birthdate"
          placeholder="Data de Nascimento"
          value={birthdate}
          onChange={e => setBirthdate(e.target.value)}
          className={error ? 'invalid' : ''}
        />
        <button type="submit">ACESSAR</button>
        {error && <p className="error">{error}</p>}
      </form>
      <a href="/reset-password">Esqueceu sua senha?</a>
    </div>
  );
}

export default LoginPage;
